package Operators;

public class ArithmaticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=17;
		int num2=7;
		System.out.println(num1+num2);
		int num3=num1-num2;
		System.out.println(num3);
		num3=num1-num2;
		System.out.println(num3);
		num3=num1/num2;
		System.out.println(num3);
		num3=num1%num2;
		System.out.println(num3);
		
		// float type
		float num11=24;
		float num22=9;
		System.out.println(num11+num22);
		
		float num33=num11-num22;
		System.out.println(num33);
		
		num33=num11-num22;
		System.out.println(num33);
		
		num33=num11/num22;
		System.out.println(num33);
		
		num33=num11%num22;
		System.out.println(num33);

	}

}
