package Operators;

public class Bitwise_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=6;
		int b=4;
		System.out.println(a&b);
		a=6;
		b=1;
		System.out.println(a&b);
		System.out.println(a^b);
		a=5;
		b=6;
		System.out.println(a|b);
		
		a=-6; // tilled
		System.out.println(~a);
		a=4;
		System.out.println(~a);

	}

}
